import React, {useState, useEffect} from 'react'
import axios from 'axios';

const Menu=({cat})=> {


  const [posts, setPosts]= useState([]);


  useEffect(()=>{
    const fetchData= async ()=>{
      try{
        const res= await axios.get(`/posts?cat${cat}`) // await get request
        setPosts(res.data)
      }catch(err){
        console.log(err)
      }
    }
    fetchData();
  },[cat])
