  //   },
  //   {
  //     id:4,
  //     title:"query number 1",
  //     desc: "blah blah blah",
  //   },
  // ];
  return (
    <div className="menu">
      <h1>Other queries</h1>
      {posts.map((post)=>(
            <div className="post" key={post.id}>
              <h2>{post.title}</h2>
              <button>Read More</button>
            </div>
          ))}
    </div>
  )
}

export default Menu
